import { useState } from 'react';
import './App.css';
// import todoListItem from './todoListItem';


function App() {

  const [taskArray, setTaskArray] = useState([]);
  const [taskText, setTaskText] = useState("");

  const addTask = () => {
    setTaskArray((prevValues) => setTaskArray([...prevValues, taskText]))
  }

  const tas = taskArray.map((task, index) => {
    return (
      <div className="todoListItem">
        <input type="checkbox" name={"completeMarker" + index} className="completed" />
        <p className="todoListText">{task}</p>
      </div>
    )
  })

  return (
    <div className="app">
      <div className="headerImage">
        <div className="headerImage"></div>
        <div className="activeContainer">
          <div className="mainContainer">
            <div className="header">
              <div className="mainHeaderBar">
                <h2 className="mainHeaderBarText">TODO</h2>
              </div>
            </div>
            <div className="listBody">
              <div className="addNewTask">
                <input type="text" name="todo" className="todoInput" id="todo" placeholder="Create a new todo..." autoComplete='off' value={taskText} onChange={(event) => setTaskText(event.target.value)} />
                <button className="addTodoButton" type="button" onClick={addTask}>Add</button>
              </div>
              <div className="listContainer">
                {taskArray.map((task, index) => {
                  return (
                    <div className="todoListItem">
                      <input type="checkbox" name={"completeMarker" + index} className="completed" />
                      <p className="todoListText">{task}</p>
                    </div>)
                })}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
