import { useState } from 'react';
import './App.css';
import TodoListItem from './todoListItem';


function App() {

  const [taskArray, setTaskArray] = useState([]);
  const [taskText, setTaskText] = useState("");

  const addTask = () => {
    setTaskArray(prevValues => [...prevValues, taskText])
    setTaskText("");
  };

  const deleteTask = (id) => {
    let newTaskArray = taskArray.filter((index) => {
      return index !== id;
    })
    setTaskArray(newTaskArray)
  }


  return (
    <div className="app">
      <div className="headerImage">
        <div className="headerImage"></div>
        <div className="activeContainer">
          <div className="mainContainer">
            <div className="header">
              <div className="mainHeaderBar">
                <h2 className="mainHeaderBarText">TODO</h2>
              </div>
            </div>
            <div className="listBody">
              <div className="addNewTask">
                <input type="text" name="todo" className="todoInput" id="todo" autoFocus autoCapitalize='words' placeholder="Create a new todo..." autoComplete='off' value={taskText.charAt(0).toUpperCase() + taskText.slice(1)} onChange={(event) => setTaskText(event.target.value)} />
                <button className="addTodoButton" type="button" onClick={addTask}>Add</button>
              </div>
              <div className="listContainer">
                {taskArray.map((task, index) => {
                  return (
                    <TodoListItem key={index} id={index} text={task} deleteItem={deleteTask} />
                  );
                })}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
